import * as React from 'react';
import {View,Text,Image} from "react-native";
import {  Button, Card} from 'react-native-paper';



const CardPost = () => (
  <Card  mode='contained' style={{width: 350, height:350,marginBottom: 15}}>

    <Card.Cover source={{ uri: 'https://placehold.co/600x400/png' }} />
    <Card.Content>
      <Text>@usertest</Text>
      <Text>Legenda do post lorem ipsum</Text>
    </Card.Content>

  </Card>
);

export default CardPost;
